(50, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-analytics-order-attribution-promotions-december-2024', 'marketing', 'en_US', 'Discover what drives your sales', 'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'stripe_orderstatus_bug_q2_2025', 'update', 'en_US', 'Action Required: Update Order Status Manager', 'Update to the latest Order Status Manager (v1.15.5+) to ensure payments are captured. Your version of WooCommerce Order Status Manager can interfere with order processing for payment gateways (including WooCommerce Stripe Gateway v9.2.0+), which may cause orders to process without capturing payment.', '{}', 'pending', 'woocommerce.com', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'storefront-customize', 'info', 'en_US', 'Projete sua loja com Storefront', 'Visite a página de configurações do Storefront para iniciar a configuração e personalização da sua loja.', '{}', 'unactioned', 'storefront', '2025-04-14 19:31:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'klaviyo_q2_2025', 'marketing', 'en_US', 'Tailor your marketing with data-driven automation', 'Deliver personalized experiences at scale, simplify your stack, and make your data work for you using Klaviyo’s best-in-class marketing extension for Woo. With advanced segmentation based on actual customer behavior, you can send email and SMS messages that drive revenue and increase loyalty — without adding complexity', '{}', 'pending', 'woocommerce.com', '2025-04-30 19:31:12', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-05-09 19:48:38', NULL, 0, 'plain', '', 0, 0, 'info');

